import React from 'react';
import './Cloud.css';

function Cloud() {
    const handleDragStart = (e) => {
        e.dataTransfer.setData("position", JSON.stringify({ x: e.clientX, y: e.clientY }));
    };

    const handleDragOver = (e) => {
        e.preventDefault();
    };

    const handleDrop = (e) => {
        const position = JSON.parse(e.dataTransfer.getData("position"));
        const offsetX = e.clientX - position.x;
        const offsetY = e.clientY - position.y;

        e.target.style.left = `${e.target.offsetLeft + offsetX}px`;
        e.target.style.top = `${e.target.offsetTop + offsetY}px`;
    };

    return (
        <div className='container-cloud'>
            <div
                className='cloud'
                draggable='true'
                onDragStart={handleDragStart}
                onDragOver={handleDragOver}
                onDrop={handleDrop}
            ></div>
        </div>
    );
}

export default Cloud;
