import React, { useState } from 'react';
import './Cloud.css';

function Cloud() {
    const [clouds, setClouds] = useState([
        { id: 1, x: 100, y: 100 },
        { id: 2, x: 200, y: 200 }
    ]);

    const handleDragStart = (e, id) => {
        e.dataTransfer.setData("id", id);
    };

    const handleDrop = (e) => {
        const id = e.dataTransfer.getData("id");
        const cloudIndex = clouds.findIndex(cloud => cloud.id.toString() === id);
        const newClouds = [...clouds];
        newClouds[cloudIndex].x = e.clientX;
        newClouds[cloudIndex].y = e.clientY;
        setClouds(newClouds);
    };

    return (
        <div className='container-cloud' onDrop={handleDrop} onDragOver={(e) => e.preventDefault()}>
            {clouds.map(cloud => (
                <div
                    key={cloud.id}
                    className='cloud'
                    style={{ left: cloud.x, top: cloud.y }}
                    draggable={true}
                    onDragStart={(e) => handleDragStart(e, cloud.id)}
                ></div>
            ))}
        </div>
    );
}

export default Cloud;
