import './Weather.css';
import React, { useEffect, useState } from "react";

function Weather () {

    const [data, setData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      navigator.geolocation.getCurrentPosition(async function(position) {
        const lat = position.coords.latitude;
        const long = position.coords.longitude;

        const apiUrl = `https://api.openweathermap.org/data/2.5/onecall?lat=${lat}&lon=${long}&units=metric&appid=${process.env.REACT_APP_API_KEY}`;

        const response = await fetch(apiUrl);
        const result = await response.json();
        setData(result);
        console.log(result);
      });
    };

    fetchData();
  }, []); // Empty dependency array ensures useEffect runs once after initial render

  if (!data) {
    return <div>Loading...</div>;
  }

    return (
    <div className="container"> 
        <div className="card">
           <form>
            <input className="input" required-type="text" id="City"></input>
            <label className="label" >Enter city</label>
            <button><span className="material-symbols-outlined">search</span></button>
           </form>
           <div className="city"></div>
           <div className="propriete"></div>
        </div>
    </div>
    )
};

export default Weather;
